import React from "react";
import useStyles from "./courseDescriptionStyles";
import { useState, useEffect } from "react";
import {
  Grid,
  Paper,
  Container,
  TextField,
  Typography,
} from "@material-ui/core";
import { useForm, Form } from "./useForm";
import Controls from "../controls/Controls";
import * as dropdownService from "../../services/dropdownData";
import RemoveIcon from "@material-ui/icons/Remove";
import AddIcon from "@material-ui/icons/Add";
import IconButton from "@material-ui/core/IconButton";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";

const OpeningReport = () => {
  const classes = useStyles();
  const [departmentName, setDepartmentName] = useState("");
  const intialFValues = {
    departmentName: "Department of Computer Science & Enginerring and IT",
    programName: "",
    semester: "",
    branch: "",
    courseName: "",
    courseCode: "",
  };
  const { values, setValues, handleInputChange } = useForm(intialFValues);
  const programOptions = [
    { id: 0, title: "B.Tech(IT)" },
    { id: 1, title: "B.Tech(CSE)" },
    { id: 2, title: "INTGT(CSE)" },
    { id: 3, title: "M.Tech(CSE)" },
    { id: 4, title: "M.Tech(DA)" },
  ];
  const semesterItems = [
    { id: 0, title: "Odd" },
    { id: 1, title: "Even" },
  ];

  return (
    <div className={classes.mainContainer}>
      <Paper className={classes.pageContent}>
        <Form>
          <Controls.Input
            name="departmentName"
            label="Department Name"
            value={values.departmentName}
            onChange={handleInputChange}
          />
          <Controls.Select
            name="programName"
            label="Program Name"
            value={values.programName}
            onChange={handleInputChange}
            options={programOptions}

            // error={errors.creditsLecture}
          />
          <Controls.RadioGroup
            name="semester"
            label="Semester"
            value={values.semester}
            onChange={handleInputChange}
            items={semesterItems}
          />
          <Controls.Select
            name="courseName"
            label="Course Name"
            value={values.courseName}
            onChange={handleInputChange}
            options={dropdownService.courseNameItems()}
            // error={errors.creditsLecture}
          />
          <Controls.Select
            name="courseCode"
            label="Course Code"
            value={values.courseCode}
            onChange={handleInputChange}
            options={dropdownService.courseCodeItems()}
            // error={errors.creditsLecture}
          />
          {coordinator.map((coordinator, index1) => (
            <div key={index1} className={classes.insideDiv}>
              <Typography className={classes.captionsText}>
                Coordinator Details
              </Typography>
              <TextField
                variant="outlined"
                label="Coordinator Name"
                name="coordinatorName"
                value={coordinator.coordinatorName}
                onChange={(event) => handleChangeCoordinator(index1, event)}
                style={{ width: "35%" }}
              />
              <TextField
                variant="outlined"
                label="Coordinator Code"
                name="coordinatorCode"
                value={coordinator.coordinatorCode}
                onChange={(event) => handleChangeCoordinator(index1, event)}
                style={{ width: "35%" }}
              />
              <IconButton
                className={classes.addButton}
                onClick={() => handleplusCoordinator()}
              >
                <AddIcon />
              </IconButton>
              <IconButton
                className={classes.minusButton}
                onClick={() => handleMinusCoordinator()}
              >
                <RemoveIcon />
              </IconButton>
            </div>
          ))}
        </Form>
      </Paper>
    </div>
  );
};

export default OpeningReport;
