import React from "react";
import { useState, useEffect } from "react";
import {
  Grid,
  TextField,
  Paper,
  FormControlLabel,
  FormControl,
  FormLabel,
  Radio,
} from "@material-ui/core";
import useStyles from "./courseDescriptionStyles";
import { useForm, Form } from "./useForm";
import Controls from "../controls/Controls";

const CourseDescription1 = () => {
  const classes = useStyles();
  const intialFValues = {
    courseName: "",
    courseCode: "",
    semester: "even",
    branch: "",
    monthFrom: "",
    monthTo: "",
    creditsLecture: "",
    creditsTutorial: "",
    creditsLab: "",
    contactHours: "",
    coordinatorNames: [],
    teacherNames: [],
    courseOutcomes: [],
    modules: [],
    eval: "",
    books: [],
  };
  const { values, setValues, handleInputChange } = useForm(intialFValues);
  const semesterItems = [
    { id: "odd", title: "odd" },
    { id: "even", title: "even" },
  ];

  return (
    <Paper className={classes.pageContent}>
      <Form>
        <Grid container>
          <Grid item xs={6}>
            <Controls.Input
              name="courseName"
              label="Course Name"
              value={values.courseName}
              onChange={handleInputChange}
            />
            <Controls.Input
              name="courseCode"
              label="Course Code"
              value={values.courseCode}
              onChange={handleInputChange}
            />
          </Grid>
          <Grid item xs={6}>
            <Controls.RadioGroup
              name="semester"
              label="Semester"
              value={values.semester}
              onChange={handleInputChange}
              items={semesterItems}
            />
          </Grid>
        </Grid>
      </Form>
    </Paper>
  );
};

export default CourseDescription1;
