import React from "react";
import { useState, useEffect } from "react";
import { Grid, Paper, Container } from "@material-ui/core";
import useStyles from "./courseDescriptionStyles";
import { useForm, Form } from "./useForm";
import Controls from "../controls/Controls";

const CourseDescription1 = () => {
  const classes = useStyles();
  const intialFValues = {
    courseName: "",
    courseCode: "",
    semester: "even",
    branch: "",
    monthFrom: "",
    monthTo: "",
    creditsLecture: "",
    creditsTutorial: "",
    creditsLab: "",
    contactHours: "",
    coordinatorNames: [],
    teacherNames: [],
    courseOutcomes: [],
    modules: [],
    eval: "",
    books: [],
  };
  const { values, setValues, handleInputChange } = useForm(intialFValues);
  const semesterItems = [
    { id: "odd", title: "odd" },
    { id: "even", title: "even" },
  ];
  const branchItems = [
    { id: "CSE", title: "CSE" },
    { id: "ECE", title: "ECE" },
    { id: "BIOTECH", title: "BIOTECH" },
  ];

  return (
    <Container>
      <Paper className={classes.pageContent}>
        <Form>
          <Grid container>
            <Grid item xs={6}>
              <Controls.Input
                name="courseName"
                label="Course Name"
                value={values.courseName}
                onChange={handleInputChange}
              />
              <Controls.Input
                name="courseCode"
                label="Course Code"
                value={values.courseCode}
                onChange={handleInputChange}
              />
            </Grid>
            <Grid item xs={6}>
              <Controls.RadioGroup
                name="semester"
                label="Semester"
                value={values.semester}
                onChange={handleInputChange}
                items={semesterItems}
              />
              <Controls.RadioGroup
                name="branch"
                label="Branch"
                value={values.branch}
                onChange={handleInputChange}
                items={branchItems}
              />
            </Grid>
          </Grid>
        </Form>
      </Paper>
    </Container>
  );
};

export default CourseDescription1;
