import React from "react";
import { useState, useEffect } from "react";
import { Grid, Paper, Container, TextField } from "@material-ui/core";
import useStyles from "./courseDescriptionStyles";
import { useForm, Form } from "./useForm";
import Controls from "../controls/Controls";
import * as dropdownService from "../../services/dropdownData";
import RemoveIcon from "@material-ui/icons/Remove";
import AddIcon from "@material-ui/icons/Add";
import IconButton from "@material-ui/core/IconButton";

const CourseDescription1 = () => {
  const classes = useStyles();
  const intialFValues = {
    courseName: "",
    courseCode: "",
    semester: "even",
    branch: "",
    dateFrom: new Date(),
    dateTo: new Date(),
    creditsLecture: "",
    creditsTutorial: "",
    creditsLab: "",
    contactHours: "",

    teacherNames: [],
    courseOutcomes: [],
    modules: [],
    eval: "",
    books: [],
  };
  const [coordinator, setCoordinator] = useState([
    {
      coordinatorName: "",
      coordinatorCode: "",
    },
  ]);
  const handleMinusCoordinator = (index1) => {
    const value = [...coordinator];
    value.splice(index1, 1);
    setCoordinator(value);
  };
  const handleplusCoordinator = () => {
    setCoordinator([
      ...coordinator,
      {
        coordinatorName: "",
        coordinatorCode: "",
      },
    ]);
  };
  const handleChangeCoordinator = (index1, event) => {
    const values = [...coordinator];
    values[index1][event.target.name] = event.target.value;
    setCoordinator(values);
  };

  const handleInputChange1 = (e) => {
    const { name, value } = e.target;
    setValues({
      ...values,
      [name]: value,
    });
  };
  const [faculty, setFaculty] = useState([
    {
      facultyName: "",
      facultyCode: "",
    },
  ]);
  const handleChangeInput = (index, event) => {
    const values = [...inputFeilds];
    values[index][event.target.name] = event.target.value;
    setInputFeilds(values);
  };
  const handleChangeFaculty = (index2, event) => {
    const values = [...faculty];
    values[index2][event.target.name] = event.target.value;
    setFaculty(values);
  };
  const handleMinusFaculty = (index2) => {
    const value = [...faculty];
    value.splice(index2, 1);
    setFaculty(value);
  };
  const handleplusFaculty = () => {
    setFaculty([
      ...faculty,
      {
        facultyName: "",
        facultyCode: "",
      },
    ]);
  };
  const { values, setValues, handleInputChange } = useForm(intialFValues);
  const semesterItems = [
    { id: "Odd", title: "Odd" },
    { id: "Even", title: "Even" },
  ];
  const branchItems = [
    { id: "Cse", title: "Cse" },
    { id: "Ece", title: "Ece" },
    { id: "Biotech", title: "Biotech" },
  ];
  const lectureCreditItems = [
    { id: 0, title: 0 },
    { id: 1, title: 1 },
    { id: 2, title: 2 },
    { id: 3, title: 3 },
    { id: 4, title: 4 },
    { id: 5, title: 5 },
    { id: 6, title: 6 },
    { id: 7, title: 7 },
    { id: 8, title: 8 },
    { id: 9, title: 9 },
  ];

  return (
    <Container>
      <Paper className={classes.pageContent}>
        <Form>
          <Grid container>
            <Grid item xs={6}>
              <Controls.Select
                name="courseName"
                label="Course Name"
                value={values.courseName}
                onChange={handleInputChange}
                options={dropdownService.courseNameItems()}
                // error={errors.creditsLecture}
              />
              <Controls.Select
                name="courseCode"
                label="Course Code"
                value={values.courseCode}
                onChange={handleInputChange}
                options={dropdownService.courseCodeItems()}
                // error={errors.creditsLecture}
              />
              {/* <Controls.Input
                name="courseName"
                label="Course Name"
                value={values.courseName}
                onChange={handleInputChange}
              /> */}
              {/* <Controls.Input
                name="courseCode"
                label="Course Code"
                value={values.courseCode}
                onChange={handleInputChange}
              /> */}
              <Controls.Select
                name="creditsLecture"
                label="Credits Lecture"
                value={values.creditsLecture}
                onChange={handleInputChange}
                options={lectureCreditItems}

                // error={errors.creditsLecture}
              />
              <Controls.Select
                name="creditsTutorial"
                label="Credits Tutorial"
                value={values.creditsTutorial}
                onChange={handleInputChange}
                options={lectureCreditItems}

                // error={errors.creditsLecture}
              />
              <Controls.Select
                name="creditsLab"
                label="Credits Lab"
                value={values.creditsLab}
                onChange={handleInputChange}
                options={lectureCreditItems}

                // error={errors.creditsLecture}
              />
              <Controls.Select
                name="contactHours"
                label="Contact Hours"
                value={values.creditsLab}
                onChange={handleInputChange}
                options={lectureCreditItems}

                // error={errors.creditsLecture}
              />
            </Grid>
            <Grid item xs={6}>
              <Controls.RadioGroup
                name="semester"
                label="Semester"
                value={values.semester}
                onChange={handleInputChange}
                items={semesterItems}
              />
              <Controls.RadioGroup
                name="branch"
                label="Branch"
                value={values.branch}
                onChange={handleInputChange}
                items={branchItems}
              />
              <Controls.DatePicker
                name="dateFrom"
                label="Date From"
                value={values.dateFrom}
                onChange={handleInputChange}
              />
              <Controls.DatePicker
                name="dateTo"
                label="Date To"
                value={values.dateTo}
                onChange={handleInputChange}
              />
              {coordinator.map((coordinator, index1) => (
                <div key={index1}>
                  <TextField
                    variant="outlined"
                    label="Coordinator Name"
                    name="coordinatorName"
                    value={coordinator.coordinatorName}
                    onChange={handleInputChange1}
                    style={{ width: "35%" }}
                  />
                  <TextField
                    variant="outlined"
                    label="Coordinator Code"
                    name="coordinatorCode"
                    value={coordinator.coordinatorCode}
                    onChange={(event) => handleChangeCoordinator(index1, event)}
                    style={{ width: "35%" }}
                  />
                  <IconButton
                    className={classes.addButton}
                    onClick={() => handleplusCoordinator()}
                  >
                    <AddIcon />
                  </IconButton>
                  <IconButton
                    className={classes.minusButton}
                    onClick={() => handleMinusCoordinator()}
                  >
                    <RemoveIcon />
                  </IconButton>
                </div>
              ))}
              {coordinator.map((coordinator, index1) => (
                <div key={index1}>
                  <TextField
                    variant="outlined"
                    label="Coordinator Name"
                    name="coordinatorName"
                    value={coordinator.coordinatorName}
                    onChange={handleInputChange1}
                    style={{ width: "35%" }}
                  />
                  <TextField
                    variant="outlined"
                    label="Coordinator Code"
                    name="coordinatorCode"
                    value={coordinator.coordinatorCode}
                    onChange={(event) => handleChangeCoordinator(index1, event)}
                    style={{ width: "35%" }}
                  />
                  <IconButton
                    className={classes.addButton}
                    onClick={() => handleplusCoordinator()}
                  >
                    <AddIcon />
                  </IconButton>
                  <IconButton
                    className={classes.minusButton}
                    onClick={() => handleMinusCoordinator()}
                  >
                    <RemoveIcon />
                  </IconButton>
                </div>
              ))}

              {/* <Controls.Input
                name="coordinatorName"
                label="Coordinator Name"
                value={values.coordinatorName}
                onChange={handleInputChange}
              />
              <Controls.Input
                name="coordinatorCode"
                label="Coordinator Code"
                value={values.courseName}
                onChange={handleInputChange}
              /> */}
            </Grid>
          </Grid>
        </Form>
      </Paper>
    </Container>
  );
};

export default CourseDescription1;
