import React, { useState } from "react";

export default function useForm(intialFValues) {
  const [values, setValues] = useState(intialFValues);
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setValues({
      ...values,
      [name]: value,
    });
  };
  return {
    values,
    setValues,
    handleInputChange,
  };
}
