import { makeStyles } from "@material-ui/core/styles";

export default makeStyles((theme) => ({
  root: {
    "& .MuiFormControl-root": {
      width: "80%",
      margin: theme.spacing(1),
    },
  },
  pageContent: {
    padding: theme.spacing(5),
    margin: theme.spacing(5),
  },
  coordinatorContainer: {
    width: "3%",
  },
  addButton: {
    marginTop: 10,
  },
  minusButton: {
    marginTop: 10,
  },
  captionsText: {
    fontFamily: "Raleway",
    fontWeight: "bold",
    color: "#8083c9",
    marginLeft: 10,
  },
  insideDiv: {
    marginTop: 10,
  },
}));
