import React from "react";
import { useState, useEffect } from "react";
import { Grid, TextField, Paper, Input } from "@material-ui/core";
import useStyle from "./courseDescriptionStyles";

const CourseDescription1 = () => {
  const classes = useStyle();
  const intialFValues = {
    courseName: "",
    courseCode: "",
    semester: "",
    branch: "",
    monthFrom: "",
    monthTo: "",
    creditsLecture: "",
    creditsTutorial: "",
    creditsLab: "",
    contactHours: "",
    coordinatorNames: [],
    teacherNames: [],
    courseOutcomes: [],
    modules: [],
    eval: "",
    books: [],
  };
  const [values, setValues] = useState(intialFValues);
  return (
    <Paper className={classes.pageContent}>
      <form className={classes.root}>
        <Grid container>
          <Grid item xs={6}>
            <TextField
              varient="outlined"
              label="Course Name:"
              value={values.courseName}
              onChange={handleInputChange}
              InputLabelProps={{ shrink: true }}
              className={classes.inputBox}
            />
            <TextField
              varient="outlined"
              label="Course Code:"
              value={values.courseCode}
              InputLabelProps={{ shrink: true }}
            />
          </Grid>
          <Grid item xs={6}></Grid>
        </Grid>
      </form>
    </Paper>
  );
};

export default CourseDescription1;
