import React from "react";
import { useState, useEffect } from "react";
import {
  Grid,
  TextField,
  Paper,
  FormControlLabel,
  FormControl,
  RadioGroup,
  FormLabel,
  Radio,
} from "@material-ui/core";
import useStyles from "./courseDescriptionStyles";
import { useForm, Form } from "./useForm";
import Input from "../controls/input";

const CourseDescription1 = () => {
  const classes = useStyles();
  const intialFValues = {
    courseName: "",
    courseCode: "",
    semester: "even",
    branch: "",
    monthFrom: "",
    monthTo: "",
    creditsLecture: "",
    creditsTutorial: "",
    creditsLab: "",
    contactHours: "",
    coordinatorNames: [],
    teacherNames: [],
    courseOutcomes: [],
    modules: [],
    eval: "",
    books: [],
  };
  const { values, setValues, handleInputChange } = useForm(intialFValues);

  return (
    <Paper className={classes.pageContent}>
      <Form>
        <Grid container>
          <Grid item xs={6}>
            <Input></Input>
            <TextField
              variant="outlined"
              label="Course Code:"
              name="courseCode"
              value={values.courseCode}
              onChange={handleInputChange}
              InputLabelProps={{ shrink: true }}
            />
          </Grid>
          <Grid item xs={6}>
            <FormControl>
              <FormLabel>Semester</FormLabel>
              <RadioGroup
                row
                name="semester"
                value={values.semester}
                onChange={handleInputChange}
              >
                <FormControlLabel value="odd" control={<Radio />} label="odd" />
                <FormControlLabel
                  value="even"
                  control={<Radio />}
                  label="even"
                />
              </RadioGroup>
            </FormControl>
          </Grid>
        </Grid>
      </Form>
    </Paper>
  );
};

export default CourseDescription1;
