import React from 'react';
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';
import useStyles from "./courseDescriptionStyles";
import {Paper} from '@material-ui/core';





const  CourseDescription1= () => {
    const courseNameOptions=[

        'Fundamentals of Distributed Systems And Cloud Computing','Computer Networks and Internet Of things','Marketing Management','Mathematics-I'
    ]
    const defaultCourseNameOption=courseNameOptions[0];
    const classes= useStyles();
    return ( 
        <div className={classes.container}>
            <Paper elevation={3}> 
           <form>
               <label>
                   Course Name: 
                   <Dropdown options={courseNameOptions} placeholder="Select an option" className={classes.courseNameDropdown} />
               </label>
           </form>
            </Paper>

        </div>
     );
}

export default CourseDescription1 ;
