import React from "react";
import { useState, useEffect } from "react";
import { Grid, Paper, Container, TextField } from "@material-ui/core";
import useStyles from "./courseDescriptionStyles";
import { useForm, Form } from "./useForm";
import Controls from "../controls/Controls";
import * as dropdownService from "../../services/dropdownData";
import RemoveIcon from "@material-ui/icons/Remove";
import AddIcon from "@material-ui/icons/Add";
import IconButton from "@material-ui/core/IconButton";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";

const CourseDescription1 = () => {
  const classes = useStyles();
  const intialFValues = {
    courseName: "",
    courseCode: "",
    semester: "even",
    branch: "",
    dateFrom: new Date(),
    dateTo: new Date(),
    creditsLecture: "",
    creditsTutorial: "",
    creditsLab: "",
    contactHours: "",

    teacherNames: [],
    modules: [],
    eval: "",
    books: [],
  };
  const [inputFeilds, setInputFeilds] = useState([
    {
      moduleName: "",
      moduleDescription: "",
      moduleLectures: "",
    },
  ]);
  const [coordinator, setCoordinator] = useState([
    {
      coordinatorName: "",
      coordinatorCode: "",
    },
  ]);
