import {
  Grid,
  Paper,
  Container,
  TextField,
  Typography,
} from "@material-ui/core";
import React from "react";
import useStyles from "./attainmentReportStyles";
import { useForm, Form } from "./useForm";
import Controls from "../controls/Controls";
import * as dropdownService from "../../services/dropdownData";
import RemoveIcon from "@material-ui/icons/Remove";
import AddIcon from "@material-ui/icons/Add";
import IconButton from "@material-ui/core/IconButton";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";

const AttainmentReport = () => {
  const classes = useStyles();
  const [attainmentLab, setAttainmentLab] = useState([
    {
      coNum: "",
      eval1: "",
      labTest1: "",
      eval28: "",
      eval27: "",
      project: "",
      labtest2: "",
      feedback: "",
    },
  ]);
  const handlePlusAttainmentLab = (index1, event) => {
    setAttainmentLab([
      ...attainmentLab,
      {
        coNum: "",
        eval1: "",
        labTest1: "",
        eval28: "",
        eval27: "",
        project: "",
        labtest2: "",
        feedback: "",
      },
    ]);
  };
  const handleMinusCourseOutcome = (index3) => {
    const value = [...courseOutcome];
    value.splice(index3, 1);
    setCourseOutcome(value);
  };
  const handleChangeCourseOutcome = (index3, event) => {
    const values = [...courseOutcome];
    values[index3][event.target.name] = event.target.value;
    setCourseOutcome(values);
  };
  return (
    <div className={classes.mainContainer}>
      <Paper className={classes.pageContent}>
        {attainmentLab.map((attainmentLab, index1) => (
          <div key={index1} className={classes.insideDiv}>
            <Typography className={classes.captionsText}>
              Average CO-Attainment
            </Typography>
            <TextField
              variant="outlined"
              label="COs"
              name="co"
              value={attainmentLab.coNum}
              onChange={(event) => handleChangeCourseOutcome(index1, event)}
              style={{ width: "10%" }}
            />
            <TextField
              variant="outlined"
              label="Eval-1[15]"
              name="eval1"
              value={attainmentLab.eval1}
              onChange={(event) => handleChangeCourseOutcome(index1, event)}
              style={{ width: "35%" }}
            />
            <TextField
              variant="outlined"
              label="Lab Test-1[20]"
              name="labTest1"
              value={attainmentLab.labTest1}
              onChange={(event) => handleChangeCourseOutcome(index1, event)}
              style={{ width: "35%" }}
            />
            <IconButton
              className={classes.addButton}
              onClick={() => handleplusCourseOutcome()}
            >
              <AddIcon />
            </IconButton>
            <IconButton
              className={classes.minusButton}
              onClick={() => handleMinusCourseOutcome()}
            >
              <RemoveIcon />
            </IconButton>
          </div>
        ))}
      </Paper>
    </div>
  );
};

export default AttainmentReport;
